(234, 60, '_wp_attached_file', '2018/04/3ac79f3df8dcd1007fde3f4e7e8b4710b9122f1b.jpg'),
(235, 60, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:5882;s:6:"height";i:3832;s:4:"file";s:52:"2018/04/3ac79f3df8dcd1007fde3f4e7e8b4710b9122f1b.jpg";s:5:"sizes";a:4:{s:9:"thumbnail";a:4:{s:4:"file";s:52:"3ac79f3df8dcd1007fde3f4e7e8b4710b9122f1b-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:52:"3ac79f3df8dcd1007fde3f4e7e8b4710b9122f1b-300x195.jpg";s:5:"width";i:300;s:6:"height";i:195;s:9:"mime-type";s:10:"image/jpeg";}s:12:"medium_large";a:4:{s:4:"file";s:52:"3ac79f3df8dcd1007fde3f4e7e8b4710b9122f1b-768x500.jpg";s:5:"width";i:768;s:6:"height";i:500;s:9:"mime-type";s:10:"image/jpeg";}s:5:"large";a:4:{s:4:"file";s:53:"3ac79f3df8dcd1007fde3f4e7e8b4710b9122f1b-1024x667.jpg";s:5:"width";i:1024;s:6:"height";i:667;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:12:{s:8:"aperture";s:1:"0";s:6:"credit";s:9:"石丕民";s:6:"camera";s:0:"";s:7:"caption";s:1257:"2017年12月18-20日，摄影家石丕民拍摄于贵州榕江萨玛节期间。萨玛是侗族人民信奉、崇拜的至高无上的女神，她代表了整个侗族共同的祖先神灵的化身，是侗族唯一共同祭祀的、本民族自己的神。同时萨玛又是古代侗族女英雄，在侗族古代社会的政治、军事、文化等方面占有重要地位。她带领侗族人民去战胜敌人、战胜自然、战胜灾难，并赢得村寨安乐、人畜兴旺的。侗家人供奉萨玛，希望在其英灵的护佑下生活安康、村寨平安。侗族地区的妇女们平时每逢农历初一、十五就会到萨玛祠来祭祀萨玛。每年都要在萨玛祠举行盛大的祭典，场面庞大而壮观，代代相传，形成了今天的“萨玛节”。 2006年，侗族“萨玛节”被列入第一批国家非物质文化遗产名录。据了解，榕江侗、苗、水、瑶四大主体少数民族占总人口84.4%，同时，各民族支系众多，独具特色，个性显明，其中侗族有8个支系，苗族有15个支系，是黔东南州侗族、苗族支系最多的县。加上处于山区，没有受到外来文化的冲击，民族文化保存完好，形成了榕江独特绚丽多彩的民族文化。";s:17:"created_timestamp";s:10:"1513595159";s:9:"copyright";s:9:"石丕民";s:12:"focal_length";s:1:"0";s:3:"iso";s:1:"0";s:13:"shutter_speed";s:1:"0";s:5:"title";s:0:"";s:11:"orientation";s:1:"0";s:8:"keywords";a:20:{i:0;s:6:"侗族";i:1;s:6:"图案";i:2;s:6:"地点";i:3;s:4:"大M";i:4;s:9:"大规模";i:5;s:12:"少数名族";i:6;s:6:"徕卡";i:7;s:6:"榕江";i:8;s:6:"歌舞";i:9;s:12:"民俗风情";i:10;s:6:"水族";i:11;s:6:"瑶族";i:12;s:15:"石丕民摄影";i:13;s:12:"石头摄影";i:14;s:6:"美女";i:15;s:6:"色彩";i:16;s:6:"节日";i:17;s:6:"苗族";i:18;s:9:"萨玛节";i:19;s:6:"贵州";}}}'),
